import { StrictMode } from "react";
import { createRoot } from "react-dom/client";
import {
  createBrowserRouter,
  RouterProvider,
  Link,
  redirect,
} from "react-router-dom";
import "./styles.css";
import "bootstrap/dist/css/bootstrap.css";
import Root from "./routes/Root";
import Index from "./routes/Index";
import Game from "./routes/Game";
import { fetchRandomWords, fetchReviews, saveReview } from "./api";
import Reviews from "./routes/Reviews";
import NewReview from "./routes/NewReview";
import { toast } from "react-toastify";

const router = createBrowserRouter([
  {
    path: "/",
    element: <Root />,
    children: [
      {
        path: "/",
        element: <Index />,
      },
      {
        path: "/about",
        element: <Index />,
      },
      {
        path: "/leaderboard",
        element: <Index />,
      },
      {
        path: "/reviews",
        element: <Reviews />,
        loader() {
          return fetchReviews();
        },
        children: [
          {
            path: "/reviews",
            element: (
              <Link to="/reviews/new" className="btn btn-primary col-3">
                Write a Review
              </Link>
            ),
          },
          {
            path: "/reviews/new",
            element: <NewReview />,
            action({ request }) {
              return request.formData().then((formData) => {
                return saveReview(
                  formData.get("title"),
                  formData.get("review"),
                  formData.get("name")
                ).then(() => {
                  toast.success("Your review was posted successfully.");
                  return redirect(`/reviews`);
                });
              });
            },
          },
        ],
      },
      {
        path: "/reviews/:reviewId/edit",
        element: <EditReview />,
        loader({ params }) {
          return fetchReview(params.reviewId);
        },
        action({ request, params }) {
          return request.formData().then((formData) => {
            return updatePost(
              params.reviewId,
              formData.get("title"),
              formData.get("review"),
              formData.get("name")
            ).then(
              () => {
                toast.success("Your review was edited successfully.");
              },
              () => {
                toast.error("Uh oh!");
              }
            );
          });
        },
      },
      {
        path: "/admin",
        element: <Index />,
      },
      {
        path: "/game/",
        element: <Game />,
        loader() {
          return fetchRandomWords();
        },
        // children: [
        //   {
        //     path: "/game/:word",
        //     element: <Question />,
        //     loader({ params }) {
        //       return fetchTranslatedWords(params.word);
        //     }
        //   }
        // ]
      },
    ],
  },
]);

const rootElement = document.getElementById("root");
const root = createRoot(rootElement);

root.render(
  <StrictMode>
    <RouterProvider router={router} />
  </StrictMode>
);
