import { useLoaderData, Link } from "react-router-dom";
import Review from "../Review";

export default function Reviews() {
  const data = useLoaderData();
  const reviews = data.map((review) => {
    return (
      <Review
        key={review.id}
        title={review.title}
        body={review.body}
        name={review.userId}
      />
    );
  });
  return (
    <>
      <h1>Reviews</h1>

      <Link to="/review/new" className="btn btn-primary col-3">
        Write a Review
      </Link>
      {reviews}
    </>
  );
}
