import { useEffect, useState } from "react";
import { Link } from "react-router-dom";

export default function Index() {
  const [userName, setUserName] = useState("");
  // const [lastName, setLastName] = useState("");
  // const [emailAdress, setEmailAdress] = useState("");
  // const [phoneNumber, setPhoneNumber] = useState("");
  // const [sleeveLength, setSleeveLength] = useState("long");
  // const [printNameState, setPrintNameState] = useState(false);
  // const [printSealState, setPrintSealState] = useState(false);
  // const [printLogoState, setPrintLogoState] = useState(false);
  // const [printYearState, setPrintYearState] = useState(false);
  // const [shirtSize, setShirtSize] = useState("");
  // const [comments, setComments] = useState("");
  // const [isCompliantWithTerms, setIsCompliantWithTerms] = useState(false);
  // const [isFormSubmitted, setIsFormSubmitted] = useState(false);

  useEffect(() => {
    document.title = "Bilingual Test Game";
  }, []);
  return (
    <>
      <h1 className="justify-content-center">Bilingual Test Game</h1>
      <form>
        <div className="form-group mb-3">
          <label htmlFor="exampleInputPassword1">Username</label>
          <input
            type="username"
            className="form-control"
            id="Username"
            placeholder="Enter Username"
          />
        </div>
        <div>
          <select className="custom-select mb-3 mr-3">
            <option defaultValue>Select Language to Translate From</option>
            <option value="Spanish">Spanish</option>
            <option value="English">English</option>
          </select>
          <select className="custom-select">
            <option defaultValue>Select Language to Translate To</option>
            <option value="Spanish">Spanish</option>
            <option value="English">English</option>
          </select>
        </div>
        <div>
          <select className="custom-select">
            <option defaultValue>Select Number of Questions</option>
            <option value="3">3</option>
            <option value="5">5</option>
            <option value="10">10</option>
          </select>
        </div>
        <Link type="submit" className="btn btn-primary" to="/game">
          Start Game
        </Link>
      </form>
    </>
  );
}
