function _fetch(url) {
  return fetch(url).then((response) => {
    return response.json();
  });
}

export function fetchRandomWords() {
  return _fetch("https://random-word-api.herokuapp.com/word?number=10");
}

export function fetchTranslatedWords(word) {
  return _fetch(
    `https://api.mymemory.translated.net/get?q=${word}!&langpair=en|es`
  );
}
export function fetchTopTracks() {
  return fetch("http://localhost:3000/reviews", {
    method: "GET",
    headers: {
      "Content-type": "application/json",
    },
  });
}
