import ChecklistItem from "./ChecklistItem";
import { useState } from "react";

export default function DeleteChecklist(props) {
  const [isBoxChecked, setIsBoxChecked] = useState([]);
  const [isCheckAll, setIsCheckAll] = useState(false);

  let checklist = [];

  //   this.items = isBoxChecked;

  props.items.forEach((item, index) => {
    if (isCheckAll) {
      setIsBoxChecked(isBoxChecked.concat(item.id));
    }
    var label;
    if (item.title) {
      label = item.title;
    } else {
      label = item.name;
    }
    checklist.push(
      <ChecklistItem
        key={index}
        id={item.id}
        label={label}
        checked={isBoxChecked.includes(item.id)}
        onChange={(isChecked) => {
          if (isChecked) {
            setIsBoxChecked(isBoxChecked.concat(item.id));
          } else {
            setIsBoxChecked(
              isBoxChecked.filter((entry) => {
                return entry !== item.id;
              })
            );
          }
        }}
      />
    );
  });

  const masterCheckbox = (
    <ChecklistItem
      key={"master"}
      id={"master"}
      label={"Select All"}
      checked={isBoxChecked.includes("master")}
      onChange={(isChecked) => {
        if (isChecked) {
          props.items.forEach((item) => {
            setIsCheckAll(true);
          });
          setIsBoxChecked(isBoxChecked.concat("master"));
        } else {
          setIsCheckAll(false);

          setIsBoxChecked([]);
        }
      }}
    />
  );
  function clickHandler() {
    let selectedItems = isBoxChecked;
    props.onClick(selectedItems.toString());
  }
  function masterClickHandler() {
    let selectedItems = isBoxChecked;
    props.onClick(selectedItems.toString());
  }
  return (
    <div style={{ color: `${props.color}` }}>
      <h1>{props.title}</h1>
      {masterCheckbox}
      <br></br>
      {checklist}
      <button onClick={masterClickHandler}>Submit</button>
    </div>
  );
}
