import { StrictMode } from "react";
import { createRoot } from "react-dom/client";
import { createBrowserRouter, RouterProvider, Link } from "react-router-dom";
import "./styles.css";
import "bootstrap/dist/css/bootstrap.css";
import Root from "./routes/Root";
import Index from "./routes/Index";
import Game from "./routes/Game";
import { fetchRandomWords, fetchReviews } from "./api";
import Reviews from "./routes/Reviews";
import NewReview from "./routes/NewReview";

const router = createBrowserRouter([
  {
    path: "/",
    element: <Root />,
    children: [
      {
        path: "/",
        element: <Index />,
      },
      {
        path: "/about",
        element: <Index />,
      },
      {
        path: "/leaderboard",
        element: <Index />,
      },
      {
        path: "/reviews",
        element: <Reviews />,
        loader() {
          return fetchReviews();
        },
        children: [
          {
            path: "/reviews",
            element: (
              <Link to="/reviews/new" className="btn btn-primary col-3">
                Write a Review
              </Link>
            ),
          },
          {
            path: "/reviews/new",
            element: <NewReview />,
            action({ request }) {
              return request.formData().then((formData) => {
                return saveReview(formData.get("review")).then(
                  () => {
                    toast.success("Your comment was successfully posted.");
                    return redirect(`/reviews`);
                  }
                );
              });
          },
        },
      ],},
      {
        path: "/admin",
        element: <Index />,
      },
      {
        path: "/game/",
        element: <Game />,
        loader() {
          return fetchRandomWords();
        },
        // children: [
        //   {
        //     path: "/game/:word",
        //     element: <Question />,
        //     loader({ params }) {
        //       return fetchTranslatedWords(params.word);
        //     }
        //   }
        // ]
      },
    ],
  },
]);

const rootElement = document.getElementById("root");
const root = createRoot(rootElement);

root.render(
  <StrictMode>
    <RouterProvider router={router} />
  </StrictMode>
);
