import { useLoaderData, redirect } from "react-router-dom";
import DeleteChecklist from "../components/DeleteChecklist";
import { deleteReview } from "./api";
import { toast } from "react-toastify";

export default function Admin(props) {
  const title = props.type;
  const data = useLoaderData();
  let color = "black";
  if (title === "Reviews") {
    color = "blue";
  }

  const clickHandler = function (selectedItems) {
    const allPromises = selectedItems.map((item) => {
      return deleteReview(item);
    });
    Promise.all(allPromises).then(() => {
      toast.success("Your comment was deleted sucessfully.");
      return redirect(`/reviews`);
    });
  };
  return (
    <div className="container">
      <DeleteChecklist
        title={title}
        items={data}
        color={color}
        onClick={(selectedItems) => {
          clickHandler(selectedItems);
        }}
      ></DeleteChecklist>
    </div>
  );
}
