function _fetch(url) {
  return fetch(url).then((response) => {
    return response.json();
  });
}

export function fetchRandomWords() {
  return _fetch("https://random-word-api.herokuapp.com/word?number=10");
}

export function fetchTranslatedWords(word) {
  return _fetch(
    `https://api.mymemory.translated.net/get?q=${word}!&langpair=en|es`
  );
}
export function fetchReviews() {
  return _fetch("http://localhost:3000/reviews");
}

export function saveReview(body, name) {
  return fetch("http://localhost:3000/reviews", {
    method: "POST",
    body: JSON.stringify({
      title: "Title",
      body: body,
      userId: "Name",
    }),
    headers: {
      "Content-type": "application/json",
    },
  }).then((response) => {
    return response.json();
  });
}
