import { createPortal } from "react-dom";
import {Link} from "react-router-dom";
import {useState, useEffect} from "react";

export default function Popupp(props) {
  // const [popupClass, setPopupClass] = useState("popup");
  const[active, setActive] = useState(props.isActive);
  const id = props.id;
  let popup = (<div className="popup">
  <h1>Delete?</h1>
  <p>Are you sure you want to delte this review?</p>
  <Link
      to={`/reviews/${id}/delete`}
      onClick={hidePopup}
      className="mx-1 btn btn-primary col-2">
      Yes
    </Link>
    <Link
      to={`/reviews`}
      onClick={hidePopup}
      className="mx-1 btn btn-primary col-2"
    >
      No
    </Link>
</div>);


  const hidePopup = function(){
    // setPopupClass("hidden");
    setActive(false);
  }
  useEffect( () => {
    popup = "";
}, [active]);

  // console.log(popupClass);

  return createPortal(
    {popup},
    document.getElementById("modal-container")
  );
}
